function getTitle (vm) {
  // 组件可以提供一个 `title` 选项
  // 此选项可以是一个字符串或函数
  const { title } = vm.$options
  if (title) {
    return typeof title === 'function'
      ? title.call(vm)
      : title
  } else {
    return 'Vue SSR Demo'
  }
}

// 服务器端mixin
const serverTitleMixin = {
  created () {
    const title = getTitle(this)
    if (title && this.$ssrContext) {
      this.$ssrContext.title = title
    }
  }
}

// 客户端mixin
const clientTitleMixin = {
  mounted () {
    const title = getTitle(this)
    if (title) {
      document.title = title
    }
  }
}

// 可以通过 `webpack.DefinePlugin` 注入 `VUE_ENV`
export default process.env.VUE_ENV === 'server'
  ? serverTitleMixin
  : clientTitleMixin

import Vue from 'vue'
import Router from 'vue-router'

Vue.use(Router)

export function createRouter () {
  return new Router({
    mode: 'history',
    linkActiveClass: 'current-active',
    linkExactActiveClass: 'exact-active',
    routes: [
      {
        path: '/',
        name: 'main',
        component: () => import('@views/Main'), // main
        children: [
          {
            path: '',
            name: 'home',
            component: () => import('@views/Home/home') // 主页
          },
          {
            path: 'column/:column_us_name',
            name: 'column',
            component: () => import('@views/Home/home') // 专栏
          },
          {
            path: 'writer',
            name: 'writer',
            component: () => import('@views/Writer/Writer') // 文章编写
          }
        ]
      },
    ]
  })
}

import { fetch } from '@request'

export default {
  PERSONAL_INFO ({ commit, dispatch, state }, parameter = {}) { // 注册 post
    return fetch({
      url: '/client/personal/info',
      method: 'post',
      parameter: {
        accessToken: parameter.accessToken
      }
    })
      .then(res => {
        commit('SET_PERSONAL_INFO', res.data)
      })
  },
  GET_ARTICLE_COLUMN ({ commit, dispatch, state }) { // 获取文章专栏 post
    return fetch({
      url: '/client/article/column',
      method: 'get',
      parameter: ''
    })
      .then(res => {
        commit('SET_ARTICLE_COLUMN', res.data)
      })
  },
  GET_HOME_BANNER ({ commit, dispatch, state }) { // 获取HOME BANNER
    return fetch({
      url: '/client/home_banner',
      method: 'get',
      parameter: ''
    })
      .then(res => {
        commit('SET_HOME_BANNER', res.data)
      })
  }
}

import Vue from 'vue'

export default {
  SET_IS_LOGIN (state, data) { // 登录弹窗控制
    state.is_login_show = data
  },
  SET_IS_REGISTER (state, data) { // 注册弹窗控制
    state.is_register_show = data
  },
  SET_IS_RESET_PASSWORD (state, data) { // 找回密码弹窗控制
    state.is_reset_password_show = data
  },
  SET_PERSONAL_INFO (state, data) { // 设置登录后的个人信息
    state.personal_info = data
  },
  SET_ARTICLE_COLUMN (state, data) { // 设置文章专栏
    state.article_column = data.list
  },
  SET_CURRENT_ARTICLE_COLUMN (state, data) { // 设置当前文章专栏
    state.c_column_us_name = data
  },
  SET_HOME_BANNER (state, data) { // home banner
    state.home_banner = data.banner
  }
}

import Vue from 'vue'
import Vuex from 'vuex'
import actions from './actions'
import mutations from './mutations'

Vue.use(Vuex)

export function createStore () {
  return new Vuex.Store({
    state: {
      is_login_show: false, // 登录弹窗是否显示
      is_register_show: false, // 注册弹窗是否显示
      is_reset_password_show: false, // 找回密码弹窗是否显示
      personal_info: {}, // 登录后的用户个人信息
      article_column: [], // 获取文章专栏
      c_column_us_name: '', // 当前 文章专栏
      home_banner: [], // home banner
    },
    actions,
    mutations
  })
}

import Vue from 'vue'
import App from './App.vue'
import TitleMixin from './mixins/title-mixins'
import { createRouter } from './router'
import { createStore } from './store'
import { sync } from 'vuex-router-sync'


import './assets/css/reset.scss'
import './assets/css/box_layout.scss'


Vue.mixin(TitleMixin)

export function createApp (context) {
  // 创建 router 和 store 实例
  const router = createRouter()
  const store = createStore()

  // 同步路由状态(route state)到 store
  sync(store, router)

  const app = new Vue({
    router,
    store,
    render: h => h(App)
  })

  return { app, router, store }
}

import { createApp } from './app'

export default context => {
  // 返回Promise 等待异步路由钩子函数或组件
  return new Promise((resolve, reject) => {
    const { app, router, store } = createApp()

    // 设置服务端router的位置
    router.push(context.url)

    // 等到 router 将可能的异步组件和钩子解析完
    router.onReady(() => {
      const matchedComponents = router.getMatchedComponents()
      //  如果匹配不到, 返回404
      if (!matchedComponents.length) {
        /* eslint-disable */
        return reject({ code: 404 })
      }
      // 对所有匹配的路由组件调用 `asyncData()`
      Promise.all(matchedComponents.map(Component => {
        if (Component.asyncData) {
          return Component.asyncData({
            store,
            route: router.currentRoute,
            accessToken: context.accessToken,
          })
        }
      }))
        .then(() => {
          // 在所有预取钩子(preFetch hook) resolve 后，
          // 我们的 store 现在已经填充入渲染应用程序所需的状态。
          // 当我们将状态附加到上下文，
          // 并且 `template` 选项用于 renderer 时，
          // 状态将自动序列化为 `window.__INITIAL_STATE__`，并注入 HTML。
          context.state = store.state
          resolve(app)
        })
        .catch(reject)
    }, reject)
  })
}
