import LRU from 'lru-cache'

const ports = require('../../config/ports')
let baseURL = `http://localhost:${process.env.NODE_ENV === 'production' ? ports.product : ports.client_dev}`

export function requestConfig() {
  let _c = {}
  _c.onServer = true
  _c.cachedItems = new LRU({
    max: 1000,
    maxAge: 1000 * 60 * 2 // 2 min cache
  })
  _c.url = baseURL
  return _c
}

// this is aliased in webpack config based on server/client build
// 通过webpack 打包时判断客户端请求和服务端请求的不同
// request_client 客户端会打包这个 中url为空所以不会添加url前缀
// request_server 服务端会打包这个 中url --
// process.env.NODE_ENV === 'production' 添加开发时的url client_dev 端口前缀
// 或者 生产环境的 product 端口前缀
import { requestConfig } from 'request-config'
import axios from 'axios'

let axios_config = {}

const api = requestConfig()

axios_config.baseURL = api.url

const logRequests = true || !!process.env.DEBUG_API

const service = axios.create(axios_config)

// warm the front page cache every 15 min
// make sure to do this only once across all requests
if (api.onServer) {
  warmCache()
}

function warmCache() {
  // fetchItems((api.cachedIds.top || []).slice(0, 30))
  setTimeout(warmCache, 1000 * 60 * 15)
}

export function fetch({ url, method, data }) {
  logRequests && console.log(`fetching ${url}...`)
  const cache = api.cachedItems
  if (cache && cache.has(url)) {
    logRequests && console.log(`cache hit for ${url}.`)
    return Promise.resolve(cache.get(url))
  } else {
    return new Promise((resolve, reject) => {
      service[method](url, data)
        .then(res => {
          const val = res.data
          if (val) val.__lastUpdated = Date.now()
          cache && cache.set(url, val)
          logRequests && console.log(`fetched ${url}.`)
          resolve(val)
        }, reject)
        .catch(reject)
    })
  }
}

import { fetch } from '@request'

export default {
  fetchMovie({ commit }, id) {
    return new Promise((resolve, reject) => {
      resolve({ id })
    }).then(res => {
      commit('setMoive', { res })
    })
  },
  getUserInfo({ commit }) {
    return fetch({
      url: '/client/user_info',
      method: 'get'
    })
      .then((res) => {
        commit('setUserInfo', res)
      })
  },
  getUser({ commit }, id) {
    return new Promise((resolve, reject) => {
      resolve({ id })
    }).then(res => {
      commit('setMoive', { res })
    })
  }
}

import Vue from 'vue'

export default {
  SET_IS_LOGIN(state, data) { // 登录弹窗控制
    state.is_login_show = data
  },
  SET_IS_REGISTER(state, data) { // 注册弹窗控制
    state.is_register_show = data
  },
  setMoive(state, { res }) {
    Vue.set(state, 'movie', res)
  },
  setUserInfo(state, res) {
    state.user_info = res
  }
}

import Vue from 'vue'
import Vuex from 'vuex'
import actions from './actions'
import mutations from './mutations'

Vue.use(Vuex)

export function createStore() {
  return new Vuex.Store({
    state: {
      is_login_show: false, // 登录弹窗是否显示
      is_register_show: false, // 注册弹窗是否显示
      movie: {},
      user_info: {}
    },
    actions,
    mutations
  })
}

import Vue from 'vue'
import App from './App.vue'
import TitleMixin from './mixins/title-mixins'
import { createRouter } from './router'
import { createStore } from './store'
import { sync } from 'vuex-router-sync'


import './assets/css/reset.scss'
import './assets/css/box_layout.scss'


Vue.mixin(TitleMixin)

export function createApp (context) {
  // 创建 router 和 store 实例
  const router = createRouter()
  const store = createStore()

  // 同步路由状态(route state)到 store
  sync(store, router)

  const app = new Vue({
    router,
    store,
    render: h => h(App)
  })

  return { app, router, store }
}

import { createApp } from './app'

export default context => {
  // 返回Promise 等待异步路由钩子函数或组件
  return new Promise((resolve, reject) => {
    const { app, router, store } = createApp()

    // 设置服务端router的位置
    router.push(context.url)

    // 等到 router 将可能的异步组件和钩子解析完
    router.onReady(() => {
      const matchedComponents = router.getMatchedComponents()
      //  如果匹配不到, 返回404
      if (!matchedComponents.length) {
        /* eslint-disable */
        return reject({ code: 404 })
      }
      // 对所有匹配的路由组件调用 `asyncData()`
      Promise.all(matchedComponents.map(Component => {
        if (Component.asyncData) {
          return Component.asyncData({
            store,
            route: router.currentRoute
          })
        }
      })).then(() => {
        // 在所有预取钩子(preFetch hook) resolve 后，
        // 我们的 store 现在已经填充入渲染应用程序所需的状态。
        // 当我们将状态附加到上下文，
        // 并且 `template` 选项用于 renderer 时，
        // 状态将自动序列化为 `window.__INITIAL_STATE__`，并注入 HTML。
        context.state = store.state
        resolve(app)
      }).catch(reject)
    }, reject)
  })
}
